import React from "react";
import Spinner from "./Spinner";

const Card = ({ loadingData, showData, weather, forecast }) => {
    // Date
    let today = new Date()
    let day = today.getDate()
    let month = today.getMonth() + 1
    let year = today.getFullYear()
    let date = day + '/' + month + '/' + year

    // URL
    let url = ""
    let iconUrl = ""

    // IconUrl
    let iconUrl3=""
    let iconUrl6=""
    let iconUrl9=""

    if (loadingData) {
        return <Spinner/>
    }
    if (showData) {
        url = "http://openweathermap.org/img/w/"
        iconUrl = url + weather.weather[0].icon + ".png"

        iconUrl3=url + forecast.list[1].weather[0].icon + ".png"
        iconUrl3=url + forecast.list[2].weather[0].icon + ".png"
        iconUrl3=url + forecast.list[3].weather[0].icon + ".png"


    }
    return (
        <div className="mt-5">
            {
                showData === true ? (

                    <div className="container">
                        <div className="card mb-3 mx-auto bg-dark text-light">
                        <div className="card">
                                    <h3 className="card-title">{weather.name}</h3>
                                    <p className="card-date">{date}</p>
                                    <h1 className="card-temp">{(weather.main.temp - 273.15).toFixed(1)}ÂºC</h1>
                                    <p className="card-desc"><img src={iconUrl} alt=""/>{weather.weather[0].description}</p>
                                    <img src="https://images.pexels.com/photos/11153721/pexels-photo-11153721.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1" className="img-fluid rounded-start" alt="" />
                                </div>
                        </div>
                    </div>

                ) : (
                    <h2 className="text-light">Sin datos</h2>
                )
            }
        </div>
    )
}

export default Card
