import React from "react";
import Spinner from "./Spinner";

const Card = ({ loadingData, showData, weather, forecast }) => {
    if (loadingData) {
        return <Spinner />
    }
    return (
        <div className="mt-5">
            {
                showData === true ? (
                    <div className="container">
                        <div className="card mb-3 mx-auto bg-dark text-light">
                            <div className="row g-0">
                                <div className="col-md-4">

                                </div>
                                <div className="col-md-8">
                                        <div className="card-body text-star mt-2">
                                            <img src="https://images.pexels.com/photos/586687/pexels-photo-586687.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1" className="img-fluid">
                                            </img>
                                        </div>
                                </div>
                            </div>
                        </div>
                    </div>

                ) : (
                    <h2 className="text-ligth">Sin datos</h2>
                )
            }
        </div>
    )
}

export default Card
